import React from "react";
import styled from "styled-components";
const Reservation = () => {
  const initalState = {
    firstName: "",
    lastName: "",
    email: "",
  };
  const [inputValue, setInputValue] = React.useState(initalState);
  const submitHandler = (e) => {
    console.log(e);
    e.preventDefault();
  };
  let requiredInfo = false;
  if (
    initalState.firstName !== "" &&
    initalState.lastName &&
    initalState.email
  ) {
    requiredInfo = true;
  }

  // const firstName = document.querySelector

  return (
    <StyledFormDiv>
      <form action="confirmed" onSubmit={submitHandler}>
        <input
          onChange={(e) =>
            setInputValue({
              ...inputValue,
              firstName: e.target.value,
            })
          }
          placeholder="First Name"
          type="text"
        />
        <input
          onChange={(e) =>
            setInputValue({
              ...inputValue,
              lastName: e.target.value,
            })
          }
          placeholder="Last Name"
          type="text"
        />
        <input
          onChange={(e) =>
            setInputValue({
              ...inputValue,
              email: e.target.value,
            })
          }
          placeholder="Email Name"
          type="text"
        />
        {requiredInfo ? (
          <button>Confirm</button>
        ) : (
          <button disabled>Confirm</button>
        )}
      </form>
    </StyledFormDiv>
  );
};

const StyledFormDiv = styled.div`
  border: 2px solid var(--color-cadmium-red);
  padding: 2.5rem;
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  input {
    margin: 1rem 0rem;
    display: block;
  }
  button {
    width: 100%;
    color: var(--color-selective-yellow);
    background: var(--color-alabama-crimson);
    border: 2px solid var(--color-cadmium-red);
    cursor: pointer;
    &:disabled {
      background: #c5c5c5;
      color: white;
    }
  }
`;
export default Reservation;
