import React from "react";
import styled from "styled-components";

const Reservation = () => {
  const [reservationId, setReservationId] = React.useState("");
  const [reservation, setReservation] = React.useState({});

  const submitHandler = (e) => {
    e.preventDefault();
    e.stopPropagation();
    // Fetch reservation

    if (reservationId.length > 31) {
      fetch(`/slingair/reservations/${reservationId}`)
        .then((res) => res.json())
        .then((data) => setReservation(data.data))

        .catch((err) => {
          console.log("Error", err);
        });
    }
  };

  return (
    <StyledDiv>
      <h1>Check your reservation:</h1>

      <StyledForm onSubmit={submitHandler}>
        <input
          onChange={(e) => setReservationId(e.target.value)}
          placeholder="Reservation ID"
          type="text"
        />
        <button>Find Reservation</button>
      </StyledForm>
      {Object.keys(reservation).length ? (
        <StyledReservationCard>
          <p>
            Reservation#: <span>{reservation.id}</span>
          </p>
          <p>
            Flight: <span>{item.flight}</span>
          </p>
          <p>
            Seat: <span>{item.seat}</span>
          </p>
          <p>
            Name: <span>{item.givenName + " " + item.surname}</span>
          </p>
          <p>
            Email: <span>{item.email}</span>
          </p>
        </StyledReservationCard>
      ) : (
        ""
      )}
    </StyledDiv>
  );
};

const StyledDiv = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  padding: 2rem;
  input {
    margin: 1rem 0rem;
    display: block;
  }
  h1 {
    padding-bottom: 4rem;
  }
`;

const StyledForm = styled.form`
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  padding-bottom: 4rem;
  button {
    color: var(--color-selective-yellow);
    background: var(--color-alabama-crimson);
    border: 2px solid var(--color-cadmium-red);
    cursor: pointer;
    &:hover {
      color: white;
    }
  }
`;
const ReservationInfo = styled.div``;

export default Reservation;
