import React from "react";

export const FlightContext = React.createContext();

const initialState = {
  allFlights: [],
  status: "loading",
};

function reducer(state, action) {
  console.log("action", action);
  switch (action.type) {
    case "loadAllFlights": {
      return {
        ...state,
        status: "loaded",
        allFlights: action.data,
      };
    }
  }
}

export const Flight = ({ children }) => {
  const [state, dispatch] = React.useReducer(reducer, initialState);

  //Functions
  const getAllFlights = (data) => {
    dispatch({ type: "loadAllFlights", flightsName: data });
  };
  return (
    <UserContext.Provider value={{ state, actions: { getAllFlights } }}>
      {children}
    </UserContext.Provider>
  );
};
