import React from "react";
import styled from "styled-components";
const Home = () => {
  return (
    <MainDiv>
      <StyledLeaderBoard>
        <h2>Leader board</h2>
      </StyledLeaderBoard>
      <StyledTopPaths>
        <h2>Top Paths</h2>
      </StyledTopPaths>
      <StyledMap>
        <h2>This will be the map</h2>
      </StyledMap>
    </MainDiv>
  );
};

const MainDiv = styled.div`
  display: grid;
  grid-gap: 2rem;

  grid-template-columns: repeat(2, 1fr);
  grid-template-rows: repeat(2, 1fr);
  width: 100vw;
`;
const Wrapper = styled.div`
  font-size: 2rem;
  height: 90vh;
  border: 1px solid red;
  display: flex;
  overflow: hidden;
  flex-wrap: wrap;
`;

const StyledLeaderBoard = styled.div`
  grid-column: 1/2;
  font-size: 2.8rem;
  height: 40vh;
  background: white;
  border: 10px solid blue;
  margin: 2rem 2rem;
`;

const StyledTopPaths = styled(StyledLeaderBoard)``;

const StyledMap = styled(StyledLeaderBoard)`
  grid-column: 2/3;
`;
export default Home;
