import React from "react";
import { flights } from "../../../backend/data";

export const FlightContext = React.createContext();

const initialState = {
  flights: [],
  status: "loading",
  flightName: "",
};

function reducer(state, action) {
  console.log("action", action);
  switch (action.type) {
    case "loadFlights": {
      return {
        ...state,
        status: "loaded",
        flights: action.flights,
      };
      console.log("updated state", newState);
      return newState;
    }
  }
}

export const FlightProvider = ({ children }) => {
  const [state, dispatch] = React.useReducer(reducer, initialState);

  //Functions
  const getAllFlights = (data) => {
    dispatch({ type: "loadFlights", flights: data });
  };

  const updateFlightNumber = (flightNumber) => {
    dispatch({ type: "selectedFlight" }, flightNumber);
  };
  return (
    <FlightContext.Provider value={{ state, actions: { getAllFlights } }}>
      {children}
    </FlightContext.Provider>
  );
};
