import React from "react";
import Plane from "./Plane";

import { FlightContext } from "../FlightContext";
import styled from "styled-components";
const { v4: uuidv4 } = require("uuid");


const SeatSelect = ({}) => {
  const {
    state: { flights, flightNumber },
    actions: { updateFlightNumber },
  } = React.useContext(FlightContext);

  //EventHandlers
  const handelFlightName = (e) => {
    console.log("event", e.target.value);
    updateFlightNumber(e.target.value);
    const selectFlight = document.querySelector("select");
    console.log("select flight", selectFlight);
    selectFlight.value = e.target.value;

    setFlightNumberState(e.target.value);
  };

  return (
    <>
      <Wrapper>
        Select your flight
        <select onChange={handelFlightName} value={flightNumberState}>
          <option value={null}>Select a flight</option>
          {flights.map((flight) => {
            return (
              <option key={uuidv4()} value={flight._id}>
                {flight._id}
              </option>
            );
          })}
        </select>
      </Wrapper>
      <h2>Select your seat and Provide your information!</h2>
      <Plane />
    </>
  );
};

const Wrapper = styled.div`
  display: flex;
  align-items: center;
  background: var(--color-cadmium-red);
  height: 110px;
  padding: var(--padding-page) 18px;
  font-family: var(--font-heading);
  color: white;
  font-size: 1.6rem;
  select {
    margin-left: 2rem;
    padding: 0.5rem;
  }
`;
export default SeatSelect;
