import React from "react";
import styled from "styled-components";

import tombstone from "../assets/tombstone.png";
import { FlightContext } from "./FlightContext";

const Confirmation = () => {
  const {
    state: { allNewReservations },
  } = React.useContext(FlightContext);
  console.log(allNewReservations);
  return (
    <Wrapper>
      {allNewReservations.map((reservation))}
      <h2>Your flight is confirmed</h2>
      <p>
        Reservation#: <span>{allNewReservations.id}</span>
      </p>
      <p>
        Flight: <span>{allNewReservations.flight}</span>
      </p>
      <p>
        Seat: <span>{allNewReservations.seat}</span>
      </p>
      <p>
        Name:{" "}
        <span>
          {allNewReservations.givenName + " " + allNewReservations.surname}
        </span>
      </p>
      <p>
        Email: <span>{allNewReservations.email}</span>
      </p>
    </Wrapper>
  );
};

const Wrapper = styled.div``;

export default Confirmation;
