import React from "react";
import styled from "styled-components";

const Reservation = () => {
  const [reservationId, setReservationId] = React.useState("");

  const submitHandler = (e) => {
    e.preventDefault();
    e.stopPropagation();
  };

  // Fetch reservation
  if (reservationId.length > 31) {
    React.useEffect(() => {
      fetch(`/slingair/reservations/${reservationId}`)
        .then((res) => res.json())
        .then((data) => console.log(data.data))

        .catch((err) => {
          console.log("Error", err);
        });
    }, []);
  }

  return (
    <StyledDiv>
      <h1>Check your reservation:</h1>

      <StyledForm onSubmit={submitHandler}>
        <input
          onChange={(e) => setReservationId(e.target.value)}
          placeholder="Reservation ID"
          type="text"
        />
        <button>Find Reservation</button>
      </StyledForm>

      <ReservationInfo>Reservation Detail</ReservationInfo>
    </StyledDiv>
  );
};

const StyledDiv = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  padding: 2rem;
  input {
    margin: 1rem 0rem;
    display: block;
  }
  h1 {
    padding-bottom: 4rem;
  }
`;

const StyledForm = styled.form`
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  padding-bottom: 4rem;
  button {
    color: var(--color-selective-yellow);
    background: var(--color-alabama-crimson);
    border: 2px solid var(--color-cadmium-red);
    cursor: pointer;
    &:hover {
      color: white;
    }
  }
`;
const ReservationInfo = styled.div``;

export default Reservation;
