import React from "react";
import styled from "styled-components";
const Reservation = () => {
  const initalState = {
    firstName: "",
    lastName: "",
    email: "",
  };
  const [inputValue, setInputValue] = React.useState(initalState);
  const submitHandler = (e) => {
    console.log(e);
    e.preventDefault();
  };

  const handelChange = (e) => {
    const value = e.target.value;
    console.log("value", value);
    setInputValue({ ...inputValue });
  };

  // const firstName = document.querySelector

  return (
    <StyledFormDiv>
      <form action="confirmed" onSubmit={submitHandler}>
        <input onChange={handelChange} name placeholder="First Name" type="text" />
        <input onChange={handelChange} placeholder="Last Name" type="text" />
        <input onChange={handelChange} placeholder="Email Name" type="text" />
        <button>Confirm</button>
      </form>
    </StyledFormDiv>
  );
};

const StyledFormDiv = styled.div`
  border: 2px solid var(--color-cadmium-red);
  padding: 2.5rem;
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  input {
    margin: 1rem 0rem;
    display: block;
  }
  button {
    width: 100%;
    color: var(--color-selective-yellow);
    background: var(--color-alabama-crimson);
    border: 2px solid var(--color-cadmium-red);
    cursor: pointer;
    &:disabled {
      background: #c5c5c5;
      color: white;
    }
  }
`;
export default Reservation;
