import React from "react";
import styled from "styled-components";
const Reservation = () => {
  return (
    <StyledDiv>
      <h1>Check your reservation:</h1>

      <StyledForm>
        <input placeholder="Reservation ID" type="text" />
        <button>Find Reservation</button>
      </StyledForm>

      <ReservationInfo>Reservation Detail</ReservationInfo>
    </StyledDiv>
  );
};

const StyledDiv = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  padding: 2rem;
  input {
    margin: 1rem 0rem;
    display: block;
  }

  h1 {
    padding-bottom: 4rem;
  }
`;

const StyledForm = styled.form`
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  padding
`;
const ReservationInfo = styled.div``;

export default Reservation;
