import React from "react";
import styled from "styled-components";
import { FlightContext } from "../FlightContext";
import { useHistory } from "react-router-dom";
const { v4: uuidv4 } = require("uuid");

const Reservation = () => {
  let history = useHistory();
  let allReservations = [];

  const {
    state: { flightNumber, selectedSeat },
    actions: { completedReservation },
  } = React.useContext(FlightContext);
  const initalState = {
    firstName: "",
    lastName: "",
    email: "",
  };

  const [inputValue, setInputValue] = React.useState(initalState);

  //Sending the data to backend
  const submitHandler = (e) => {
    e.preventDefault();
    e.stopPropagation();
    //call the end point
    //Check for number of selected seats and generate enough reservatins
    selectedSeat.map((everySeat) => {
      const requestReservation = {
        id: uuidv4(),
        flight: flightNumber,
        seat: everySeat,
        givenName: inputValue.firstName,
        surname: inputValue.lastName,
        email: inputValue.email,
      };
      allReservations.push(requestReservation);
      fetch("/slingair/reservations", {
        method: "POST",
        body: JSON.stringify(requestReservation),
        headers: {
          Accept: "application/json",
          "Content-Type": "application/json",
        },
      });
    });
    history.push("/confirmed");
    completedReservation(allReservations);
    //
    //Store Items to local storage
    allReservations.forEach((reserv) => {
      localStorage.setItem("reservations", allReservations);

    });
    //
    //
  };
  let requiredInfo = false;
  if (
    inputValue.firstName !== "" &&
    inputValue.lastName !== "" &&
    inputValue.email !== "" &&
    flightNumber !== "" &&
    selectedSeat.length > 0
  ) {
    requiredInfo = true;
  }

  return (
    <StyledFormDiv>
      <form onSubmit={submitHandler}>
        <input
          onChange={(e) =>
            setInputValue({
              ...inputValue,
              firstName: e.target.value,
            })
          }
          placeholder="First Name"
          type="text"
        />
        <input
          onChange={(e) =>
            setInputValue({
              ...inputValue,
              lastName: e.target.value,
            })
          }
          placeholder="Last Name"
          type="text"
        />
        <input
          onChange={(e) =>
            setInputValue({
              ...inputValue,
              email: e.target.value,
            })
          }
          placeholder="Email Name"
          type="text"
        />
        {requiredInfo ? (
          <button>Confirm</button>
        ) : (
          <button disabled>Confirm</button>
        )}
      </form>
    </StyledFormDiv>
  );
};

const StyledFormDiv = styled.div`
  border: 2px solid var(--color-cadmium-red);
  padding: 2.5rem;
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  input {
    margin: 1rem 0rem;
    display: block;
  }
  button {
    width: 100%;
    color: var(--color-selective-yellow);
    background: var(--color-alabama-crimson);
    border: 2px solid var(--color-cadmium-red);
    cursor: pointer;
    &:disabled {
      background: #c5c5c5;
      color: white;
      cursor: not-allowed;
    }
  }
`;
export default Reservation;
