import React from "react";

export const FlightContext = React.createContext();

const initialState = {
  flights: [],
  status: "loading",
  flightNumber: "",
};

function reducer(state, action) {
  switch (action.type) {
    case "loadFlights": {
      return {
        ...state,
        status: "loaded",
        flights: action.flights,
      };
    }
    case "selectedFlight": {
      return {
        ...state,
        status: "flightSelected",
        flightNumber: action.data,
      };
    }
  }
}

export const FlightProvider = ({ children }) => {
  const [state, dispatch] = React.useReducer(reducer, initialState);

  //Functions
  const getAllFlights = (data) => {
    dispatch({ type: "loadFlights", flights: data });
  };

  const updateFlightNumber = (data) => {
    dispatch({ type: "selectedFlight", data });
  };
  return (
    <FlightContext.Provider
      value={{ state, actions: { getAllFlights, updateFlightNumber } }}
    >
      {children}
    </FlightContext.Provider>
  );
};
