import React from "react";
import styled from "styled-components";
import tombstone from "../assets/tombstone.png";
import { FlightContext } from "./FlightContext";
const { v4: uuidv4 } = require("uuid");

const Confirmation = () => {
  const {
    state: { allNewReservations },
  } = React.useContext(FlightContext);
  console.log(allNewReservations);
  return (
    <Wrapper>
      <h2>Your flight is confirmed</h2>
      {allNewReservations.map((item) => {
        return (
          <Card key={uuidv4()}>
            <StyledReservationCard>
              <p>
                Reservation#: <span>{item.id}</span>
              </p>
              <p>
                Flight: <span>{item.flight}</span>
              </p>
              <p>
                Seat: <span>{item.seat}</span>
              </p>
              <p>
                Name: <span>{item.givenName + " " + item.surname}</span>
              </p>
              <p>
                Email: <span>{item.email}</span>
              </p>
            </StyledReservationCard>
            <img src={tombstone} alt="" />;
          </Card>
        );
      })}
    </Wrapper>
  );
};

const Wrapper = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  img {
    width: 100px;
  }
`;
const Card = styled.div``;``
const StyledReservationCard = styled.div`
  font-size: 1.5rem;
  padding: 1rem;
  border: 2px solid var(--color-alabama-crimson);
  margin: 1rem;
  p {
    padding: 1rem 0rem;
  }
  span {
    padding-left: 1rem;
  }
`;
export default Confirmation;
